mapper.select:
SELECT id, res_date, res_time, cake_id, cake_size, contact
FROM reservations
WHERE id = #{id}

mapper.update:
UPDATE reservations
res_date = #{resDate}, res_time = #{resTime}, contact = #{contact}, is_paid = #{paid}, status = #{status}, note = #{note}, updated_at = NOW()
WHERE id = #{id}

